import "core-js/modules/es.array.push.js";
export default {
  name: "ListItem",
  data() {
    return {
      search: null,
      dialog: false,
      dialogEdit: false,
      editStatus: false,
      deleteStatus: false,
      deleteIndex: -1,
      editIndex: -1,
      headers: [{
        text: "Nama",
        align: "start",
        sortable: true,
        value: "nama"
      }, {
        text: "Calories",
        value: "calories"
      }, {
        text: "Note",
        value: "note"
      }, {
        text: "Status",
        value: "status"
      }, {
        text: "Actions",
        value: "actions"
      }],
      todos: [{
        nama: "Fried Rice",
        calories: "120",
        note: "Tidak pakai cabe",
        status: "On Process"
      }, {
        nama: "Dessert",
        calories: "350",
        note: "Tambah alat makan",
        status: "Ready"
      }, {
        nama: "Ice Cream",
        calories: "450",
        note: "Rasa coklat dan stroberi aja",
        status: "Delivery"
      }],
      formTodo: {
        nama: null,
        calories: null,
        note: null,
        status: null
      },
      editTodo: {
        nama: null,
        calories: null,
        note: null,
        status: null
      }
    };
  },
  methods: {
    save() {
      this.todos.push(this.formTodo);
      this.resetForm();
      this.dialog = false;
    },
    // EDIT CODE
    editItem(item) {
      this.editIndex = this.todos.indexOf(item);
      this.formTodo.nama = item.nama;
      this.formTodo.calories = item.calories;
      this.formTodo.note = item.note;
      this.formTodo.status = item.status;
      this.editStatus = true;
    },
    cancelEdit() {
      this.editStatus = false;
    },
    confirmEdit() {
      this.todos[this.editIndex].nama = this.formTodo.nama;
      this.todos[this.editIndex].calories = this.formTodo.calories;
      this.todos[this.editIndex].note = this.formTodo.note;
      this.todos[this.editIndex].status = this.formTodo.status;
      this.editStatus = false;
      this.resetForm();
      this.editIndex = -1;
    },
    // END of EDIT CODE
    cancel() {
      this.resetForm();
      this.dialog = false;
    },
    // DELETE CODE
    deleteItem(item) {
      this.deleteIndex = this.todos.indexOf(item);
      this.deleteStatus = true;
    },
    cancelDelete() {
      this.deleteStatus = false;
    },
    confirmDelete() {
      this.todos.splice(this.deleteIndex, 1);
      this.deleteStatus = false;
    },
    // END of DELETE CODE
    resetForm() {
      this.formTodo = {
        nama: null,
        calories: null,
        note: null,
        status: null
      };
    }
  }
};
